StructuredTextModel.Text:
Enable_F(Clk:=Enable);
Timer_ON(In:=Timer_Active, PT:=T#50s);

IF (Enable) THEN
	Total_Time := Timer_ON.ET;
	Timer_Active := TRUE;
	ENO := TRUE;
END_IF;

IF (Enable_F.Q) THEN
	Total_Time := Timer_ON.ET;
END_IF;

IF (NOT Enable) THEN
	Timer_Active := FALSE;
	ENO := FALSE;
END_IF;

IF (Reset) THEN
	Total_Time := T#0s;
END_IF;
