StructuredTextModel.Text:
PI := 3.141592653589793238462643383279502884197;
// Verify the ratio after the gearbox - since the encoder is also after the gearbox
Feed_Verification_Data.After_Gearbox_Ratio := Metric_To_Imperial(Feeder_Scale_Ratio)*Feed_Verification_Data.Gearbox;

// Diameter of the entry feeder roller
Entry_Feeder_Dia := Metric_To_Imperial((Feeder_Scale_Ratio*Feed_Verification_Data.After_Gearbox_Ratio)/PI);

// Ratio between the diameter of the roller of the feeder and the roller of the encoder
Feeder_Encoder_Ratio := Feed_Verification_Data.Encoder_Diameter/Entry_Feeder_Dia;

// Determine the ratio in pulses between the encoder and the feeder
Feed_Verification_Data.Pulse_Ratio := SafeDiv_LREAL(Feed_Verification_Data.Pulse_Per_Rotation, (Feed_Verification_Data.After_Gearbox_Ratio*(Feeder_Encoder_Ratio)));
