StructuredTextModel.Text:
//Initialization of the utilized variables
Range_Div_By_Two := Range/2;
Range_Upper_Limit := Ref+Range_Div_By_Two;
Range_Lower_Limit := Ref-Range_Div_By_Two;

//Check if our position is within spicified range.

IF(Act_Pos >= Range_Upper_Limit) OR (Act_Pos <= Range_Lower_Limit)THEN
	Within_Range := TRUE;
ELSE
	Within_Range := FALSE;
END_IF;
