StructuredTextModel.Text:
Recipe_Transfered_RT(Clk:=HMI_Feeder_Data.Reset_Sequence);


IF (Recipe_Transfered_RT.Q) OR (NOT Master_Feeder_Options.Sequential_Feeding) THEN
	Act_Sequences := HMI_Feeder_Sequences_Retained;
	Sequence_Transfered_Latch := TRUE;
	Feed_Sequence_Changed := FALSE;
END_IF;


Feed_Sequence_Changed := NOT (Act_Sequences.Name = HMI_Feeder_Sequences_Retained.Name) OR Feed_Sequence_Changed;
Feed_Sequence_Changed := NOT (Act_Sequences.Vel = HMI_Feeder_Sequences_Retained.Vel) OR Feed_Sequence_Changed;
Feed_Sequence_Changed := NOT (Act_Sequences.Accel = HMI_Feeder_Sequences_Retained.Accel) OR Feed_Sequence_Changed;
Feed_Sequence_Changed := NOT (Act_Sequences.Decel = HMI_Feeder_Sequences_Retained.Decel) OR Feed_Sequence_Changed;
Feed_Sequence_Changed := NOT (Act_Sequences.No_Of_Pieces = HMI_Feeder_Sequences_Retained.No_Of_Pieces) OR Feed_Sequence_Changed;

FOR I:=0 TO (SizeOfAry(Act_Sequences.Enabled)) DO
	// Provide a way to showcase if any changes were made to the sequences
	Feed_Sequence_Changed := NOT (Act_Sequences.Enabled[I] = HMI_Feeder_Sequences_Retained.Enabled[I]) OR Feed_Sequence_Changed;
	Feed_Sequence_Changed := NOT (Act_Sequences.Gag[I] = HMI_Feeder_Sequences_Retained.Gag[I]) OR Feed_Sequence_Changed;
	Feed_Sequence_Changed := NOT (Act_Sequences.Distance[I] = HMI_Feeder_Sequences_Retained.Distance[I]) OR Feed_Sequence_Changed;
	
	// Prevent enabling of sequences past the one right after the last enabled one.
	IF (I > 1) AND (NOT HMI_Feeder_Sequences_Retained.Enabled[I-1]) THEN
		HMI_Feeder_Sequences_Retained.Enabled[I] := FALSE;
	END_IF;
END_FOR;
	
HMI_Feeder_Sequences_Retained.No_Of_Steps := Act_Sequences.No_Of_Steps;
