StructuredTextModel.Text:
Error := FALSE;
CompareFailed := FALSE;
NodeDatCmp_Read_Write := TRUE; // Set to false if differences in any node are found.

IF (NoOfSDOs <= SizeOfAry(NodeDat[0].SdoList)) THEN
	FOR i := LOWER_BOUND(NodeDat, 1) TO UPPER_BOUND(NodeDat, 1) DO
		IF NodeDat[i].InUse THEN
			NodeDat[i].IsEqual_ReadDat_WriteDat := TRUE;
			FOR j := 0 TO (NoOfSDOs - 1) DO
				NodeDatCmp_Read_Write := TRUE; // Set to false if differences in node are found.
				NodeDat[i].SdoList[j].IsEqual_ReadDat_WriteDat := TRUE;
				FOR k := 0 TO 7 DO
					IF NodeDat[i].SdoList[j].ReadDat[k] <> NodeDat[i].SdoList[j].WriteDat[k] THEN
						NodeDat[i].IsEqual_ReadDat_WriteDat := FALSE;
						NodeDat[i].SdoList[j].IsEqual_ReadDat_WriteDat := FALSE;
						CompareFailed := TRUE;
						NodeDatCmp_Read_Write := FALSE;
					END_IF;
				END_FOR;
			END_FOR;
		END_IF;
	END_FOR;
ELSE
	Error := TRUE;
	NodeDatCmp_Read_Write := FALSE;
END_IF;
