StructuredTextModel.Text:
(* 6 Ton : Half Stroke = 0.625, Pitman Length = 6.875 
   22 Ton : Half Stroke = 1.5,	Pitman Length = 15
  *)
A := Half_Stroke+Pitman_Length;
B := (DEG-180)*2*PI/360;
C := EXPT(Half_Stroke, LREAL#2);
E := SQRT(EXPT(Pitman_Length, 2)-EXPT(Half_Stroke, 2)*(EXPT(SIN(B), 2)));
F := (E+Half_Stroke*COS(B));

Convert_Deg_To_Height := A-(SQRT(EXPT(Pitman_Length, 2)-EXPT(Half_Stroke, 2)*(EXPT(SIN(B), 2)))+Half_Stroke*COS(B));
