StructuredTextModel.Text:
Enable_R(Clk:=Enable);
Amount_Of_Sequence:=SizeOfAry(Sequence);
IF (Enable_R.Q)THEN
	
	Found_Last_Flag :=FALSE;
	
	FOR I := 0 TO Amount_Of_Sequence BY 1 DO
		IF (Sequence[I].Punch = 0 AND Sequence[I].Feed = 0 AND NOT Found_Last_Flag) THEN
			last := I-1;
			Found_Last_Flag := TRUE;
		END_IF;
	END_FOR;
END_IF;
