StructuredTextModel.Text:
Enable_R(Clk:=Enable);

Size := Sequence_No;



IF(Enable_R.Q) THEN
//Initialization of the monitoring variable
Gag_Amount_In_Seq := 0;
// Loop Through the sequence to find the amount of Gag_To_Count
	IF(Sequence_No>0) THEN
		FOR I := 0 TO Size BY 1 DO
			Strip_UINT(Gag_Array[I], Gag_List);
			Gag_No_Extract(Gag_List, Gag_No);
			IF (Gag_No[0] = Gag_To_Count OR Gag_No[1] = Gag_To_Count) THEN
				Gag_Amount_In_Seq :=  Gag_Amount_In_Seq +1;
			END_IF;
		END_FOR;
	END_IF;
END_IF;
