StructuredTextModel.Text:
IF (Enable) THEN
	// Setup the fault timer
	Fault_Timer(IN:=Run_Timer, PT:=Frac_To_Time(Discrepency));
	
	// Fault condition
	IF (IN_NC AND IN_NO) OR
		(NOT IN_NC) AND (NOT IN_NO) THEN
		Run_Timer := TRUE;
	ELSE
		Run_Timer := FALSE;
	END_IF;
	Error := Fault_Timer.Q;
	
	// Conditions to give the output
	IF (NOT Run_Timer) AND (IN_NC AND NOT IN_NO) THEN
		Out := TRUE;
	ELSE
		Out := FALSE;
	END_IF;
ELSE
	Error := FALSE;
	Out := FALSE;
	Run_Timer := FALSE;
END_IF;
