StructuredTextModel.Text:
CU_R(Clk := CU);
Reset_R(Clk := Reset);
 Q := FALSE;

IF (CV < PV) THEN
	// Check if multiplier value is larger than one
	IF (MP <= 0) THEN
		Add_Val := 1;
	ELSE
		Add_Val := MP;
	END_IF;

	// Add multiplier value, or one, to the count
	IF (CU_R.Q) THEN
		CV := CV + Add_Val;
	END_IF;
ELSE
	Q := TRUE;
END_IF;

IF (Reset_R.Q) THEN
	CV := 0;
END_IF;
