StructuredTextModel.Text:
Enable_RT(Clk:=Enable);

IF (Enable_RT.Q)  THEN
	Total_Length := Total_Length + Length;
END_IF;

IF (Reset) THEN
	Total_Length := 0;
END_IF;

IF (Total_Length >= Length_SP*12) THEN
	Length_Reached := TRUE;
ELSE
	Length_Reached := FALSE;
END_IF;

Curr_Length_Raw := Total_Length;

// Transform units into feet or meters
IF (NOT Unit) THEN
	Curr_Length := (Total_Length+Coil_Offset_Length)/12;
ELSE
	Curr_Length := (Total_Length+Coil_Offset_Length)/1000;
END_IF;
