StructuredTextModel.Text:
IF (TestABit(Operating_Mode, 0)) THEN
	Operating_Mode_Msg := 'Pedal; ';
ELSE
	Operating_Mode_Msg := 'Palm; ';
END_IF;

IF (TestABit(Operating_Mode, 1)) THEN
	Operating_Mode_Msg := CONCAT(Operating_Mode_Msg, 'Auto; ');
ELSE
	Operating_Mode_Msg := CONCAT(Operating_Mode_Msg, 'Single; ');
END_IF;

IF (TestABit(Operating_Mode, 2)) THEN
	Operating_Mode_Msg := CONCAT(Operating_Mode_Msg, 'Automatic Stroke; ');
END_IF;

IF (TestABit(Operating_Mode, 3)) THEN
	Operating_Mode_Msg := CONCAT(Operating_Mode_Msg, 'Feeder Activation; ');
END_IF;

IF (TestABit(Operating_Mode, 4)) THEN
	Operating_Mode_Msg := CONCAT(Operating_Mode_Msg, 'Continuous on demand; ');
END_IF;

IF (TestABit(Operating_Mode, 5)) THEN
	Operating_Mode_Msg := CONCAT(Operating_Mode_Msg, 'Cut to length; ');
END_IF;
