StructuredTextModel.Text:
IF NOT ((Angle3 = Angle4)) THEN
	(* Zone does not pass through 0 *)
	IF (Angle1 <= Angle2) THEN
		IF (Angle3 >= Angle1 AND Angle3 <= Angle2) OR
			(Angle4 >= Angle1 AND Angle4 <= Angle2) THEN
			In_Zone := TRUE;
		ELSE
			In_Zone := FALSE;
		END_IF;
	ELSE
		(* Zone passes through 0 *)
		IF (Angle3 >= Angle1 OR Angle3 <= Angle2) OR
			(Angle4 >= Angle1 OR Angle4 <= Angle2) THEN
			In_Zone := TRUE;
		ELSE
			In_Zone := FALSE;
		END_IF;
	END_IF;
ELSE
	In_Zone := FALSE;
END_IF;
