StructuredTextModel.Text:
// Establish the triggers.
Start_RT(CLK:=Feed_Start);
Start_FT(CLK:=Feed_Start);
Release_RT(CLK:=Release);

// Record position on start and initialize torque value
IF (Start_RT.Q) THEN
	Initial_Pos := Pos;
	Torque := 0;
END_IF;

// ADd total torque together
IF (Trq > Torque) THEN
	Torque := Trq;
END_IF;

// Determine distance traveled and add to the total length fed
IF (Start_FT.Q) THEN
	// Determine difference between Initial pos and current pos
	Total_Distance := ABS(Initial_Pos-Pos);
	// Add the difference to the total length advanced
	Analytics.Total_Length_Advanced := Analytics.Total_Length_Advanced+Total_Distance;
	
	// Add a feed to the total feeds
	Analytics.Total_Feeds := Analytics.Total_Feeds+1;
	
	// Torque during the move
	Analytics.Torque_Value := Torque;
END_IF;

// Add to the total amount of pilot releases
IF (Release_RT.Q) THEN
	Analytics.Total_Pilot_Releases := Analytics.Total_Pilot_Releases+1;
END_IF;

// Reset the total feeds 
IF (Analytics.Reset_Total_Feeds) THEN
	Analytics.Total_Feeds := 0;
END_IF;

// Reset the total releases
IF (Analytics.Reset_Total_Releases) THEN
	Analytics.Total_Pilot_Releases := 0;
END_IF;

// Reset the total distance fed 
IF (Analytics.Reset_Length_Advanced) THEN
	Analytics.Total_Length_Advanced := 0;
END_IF;
