StructuredTextModel.Text:
IF (Enable) THEN
	IF (Metric) THEN
		Unit_ID := ID;
		Unit_OD := OD;
		// Density is in kg/in^3
		Unit_Density := Density;
		Unit_Width := Width;
		Unit_Thickness := Thickness;
	ELSE
		Unit_ID := Imperial_To_Metric(ID);
		Unit_OD := Imperial_To_Metric(OD);
		// Density is in lb/in^3
		Unit_Density := Density*27.6799;
		Unit_Width := Imperial_To_Metric(Width);
		Unit_Thickness := Imperial_To_Metric(Thickness);
	END_IF;		
	
	// Difference between the inner and outer diameters
	Diff_Cyl := (PI/4)*((Unit_OD**2)-(Unit_ID**2));
	
	// Calculate volume from coil width
	Volume := Unit_Width*Diff_Cyl;
	
	// Calulate length from coil thickness in meters
	Unit_Length := (Diff_Cyl/Unit_Thickness)/1000;
	// Calulate weight from density in kilograms
	Unit_Weight := (Volume*Unit_Density)/1000000; 
	
	// Convert units if necessary
		IF (Metric) THEN
			Length := Unit_Length;
			Weight := Unit_Weight;
		ELSE
			Length := Unit_Length*Meters_To_Feet;
			Weight := Unit_Weight*Kilograms_To_Pounds;
		END_IF;
END_IF;
